mapper.update:
UPDATE BOARD
SET B_SUBJECT = #{b_subject}
   ,B_CONTENT = #{b_content}
   ,B_UPDATEDATE = SYSDATE
WHERE B_NUM = #{b_num}
AND B_DELETEYN = 'Y'

mapper.select:
SELECT A.B_LIKE B_LIKE
FROM BOARD A
WHERE A.B_NUM = #{b_num}

SELECT LIKE_DELETEYN
FROM HEART
WHERE B_NUM = #{b_num} AND M_NUM= #{m_num}

mapper.update:
UPDATE HEART
SET LIKE_DELETEYN = 'Y'
WHERE B_NUM = #{b_num} AND M_NUM = #{m_num}

UPDATE HEART
SET LIKE_DELETEYN = 'N'
WHERE B_NUM = #{b_num} AND M_NUM = #{m_num}
